import { initializeApp, type FirebaseApp } from 'firebase/app';
import { getAuth, connectAuthEmulator, type Auth } from 'firebase/auth';
import { getFirestore, connectFirestoreEmulator, type Firestore } from 'firebase/firestore';
import { getStorage, type FirebaseStorage } from 'firebase/storage';

// Firebase config type
interface FirebaseConfig {
  apiKey: string;
  authDomain: string;
  projectId: string;
  storageBucket: string;
  messagingSenderId: string;
  appId: string;
}

// Extended Firestore type with our custom property
interface ExtendedFirestore extends Firestore {
  type: 'firestore' | 'offline';
}

// Read Firebase config from environment variables
// Support both single config object and individual variables
let firebaseConfig: FirebaseConfig = {} as FirebaseConfig;

// Try to parse config from single variable first
if (import.meta.env.VITE_FIREBASE_CONFIG) {
  try {
    firebaseConfig = JSON.parse(import.meta.env.VITE_FIREBASE_CONFIG) as FirebaseConfig;
  } catch (e) {
    console.warn('Failed to parse VITE_FIREBASE_CONFIG, trying individual variables');
  }
}

// If no config or parse failed, try individual variables
if (!firebaseConfig.apiKey) {
  firebaseConfig = {
    apiKey: import.meta.env.VITE_FIREBASE_API_KEY || '',
    authDomain: import.meta.env.VITE_FIREBASE_AUTH_DOMAIN || '',
    projectId: import.meta.env.VITE_FIREBASE_PROJECT_ID || '',
    storageBucket: import.meta.env.VITE_FIREBASE_STORAGE_BUCKET || '',
    messagingSenderId: import.meta.env.VITE_FIREBASE_MESSAGING_SENDER_ID || '',
    appId: import.meta.env.VITE_FIREBASE_APP_ID || ''
  };
}

// Initialize Firebase with deferred heavy operations
let app: FirebaseApp | null = null;
let auth: Auth | ReturnType<typeof createOfflineAuth> | null = null;
let db: ExtendedFirestore | ReturnType<typeof createOfflineDb> | null = null;
let storage: FirebaseStorage | ReturnType<typeof createOfflineStorage> | null = null;
let isOfflineMode = false;
let initialized = false;

// Offline mode implementations
const createOfflineAuth = () => ({
  currentUser: null,
  onAuthStateChanged: (callback: (user: null) => void) => {
    callback(null);
    return () => {}; // unsubscribe function
  },
  signInWithEmailAndPassword: () => Promise.reject(new Error('Offline mode')),
  createUserWithEmailAndPassword: () => Promise.reject(new Error('Offline mode')),
  signOut: () => Promise.resolve()
});

const createOfflineDb = () => ({
  type: 'offline' as const,
  collection: () => ({
    add: () => Promise.reject(new Error('Offline mode - use localStorage')),
    doc: () => ({
      get: () => Promise.reject(new Error('Offline mode - use localStorage')),
      set: () => Promise.reject(new Error('Offline mode - use localStorage')),
      update: () => Promise.reject(new Error('Offline mode - use localStorage')),
      delete: () => Promise.reject(new Error('Offline mode - use localStorage'))
    })
  })
});

const createOfflineStorage = () => ({
  ref: () => ({
    put: () => Promise.reject(new Error('Offline mode - use localStorage')),
    getDownloadURL: () => Promise.reject(new Error('Offline mode - use localStorage'))
  })
});

// Function to initialize Firebase lazily
const initializeFirebase = () => {
  if (initialized) return;

  try {
    // Check if Firebase config is valid
    if (!firebaseConfig.apiKey || firebaseConfig.apiKey === 'your-api-key') {
      console.info('📱 ALF Coach running in offline mode (Firebase not configured)');
      console.info('💡 To enable cloud sync, see: docs/firebase-setup.md');
      isOfflineMode = true;

      // Create offline implementations
      auth = createOfflineAuth();
      db = createOfflineDb() as any;
      storage = createOfflineStorage() as any;
    } else {
      // Initialize Firebase normally
      app = initializeApp(firebaseConfig);
      auth = getAuth(app);
      db = getFirestore(app) as ExtendedFirestore;
      storage = getStorage(app);

      // Add type identifier for Firestore
      (db as ExtendedFirestore).type = 'firestore';

      console.info('✅ Firebase initialized successfully');

      // Optional: Connect to emulators in development
      if (import.meta.env.DEV && import.meta.env.VITE_USE_EMULATORS === 'true') {
        connectAuthEmulator(auth, 'http://localhost:9099');
        connectFirestoreEmulator(db, 'localhost', 8080);
        console.info('🔧 Connected to Firebase emulators');
      }
    }
    initialized = true;
  } catch (error: any) {
    console.warn('⚠️ Firebase initialization failed, switching to offline mode:', error.message);
    isOfflineMode = true;

    // Provide offline implementations
    auth = createOfflineAuth();
    db = createOfflineDb() as any;
    storage = createOfflineStorage() as any;
    initialized = true;
  }
};

// Initialize immediately for now (can be made lazy later if needed)
initializeFirebase();

// Export services and offline mode status
export { auth, db, storage, isOfflineMode };

// src/hooks/useAuth.js

import { useState, useEffect, useCallback } from 'react';
import {
  onAuthStateChanged,
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signInWithPopup,
  GoogleAuthProvider,
  OAuthProvider, // For Apple and Microsoft
  signInAnonymously,
  signOut,
} from 'firebase/auth';
import { auth } from '../firebase/firebase';

/**
 * A custom React hook to manage the user's authentication state and actions.
 * It now handles multiple sign-in methods and provides a clear auth status.
 */
export const useAuth = () => {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // The onAuthStateChanged listener is the single source of truth for the user's auth state.
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setUser(user);
      setIsLoading(false);
    });

    // Cleanup subscription on unmount
    return () => unsubscribe();
  }, []);

  // --- Email & Password Methods ---
  const signUpWithEmail = useCallback(async (email, password) => {
    setError(null);
    try {
      await createUserWithEmailAndPassword(auth, email, password);
    } catch (err) {
      setError(err.message);
      console.error("Sign up failed:", err);
    }
  }, []);

  const signInWithEmail = useCallback(async (email, password) => {
    setError(null);
    try {
      await signInWithEmailAndPassword(auth, email, password);
    } catch (err) {
      setError(err.message);
      console.error("Sign in failed:", err);
    }
  }, []);

  // --- Social & Anonymous Methods ---
  const signInWithGoogle = useCallback(async () => {
    setError(null);
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (err) {
      setError(err.message);
      console.error("Google sign in failed:", err);
    }
  }, []);

  const signInWithApple = useCallback(async () => {
    setError(null);
    const provider = new OAuthProvider('apple.com');
    try {
      await signInWithPopup(auth, provider);
    } catch (err) {
      setError(err.message);
      console.error("Apple sign in failed:", err);
    }
  }, []);

  const signInWithMicrosoft = useCallback(async () => {
    setError(null);
    const provider = new OAuthProvider('microsoft.com');
    try {
      await signInWithPopup(auth, provider);
    } catch (err) {
      setError(err.message);
      console.error("Microsoft sign in failed:", err);
    }
  }, []);

  const continueAsGuest = useCallback(async () => {
    setError(null);
    try {
      await signInAnonymously(auth);
    } catch (err) {
      setError(err.message);
      console.error("Anonymous sign-in failed:", err);
    }
  }, []);

  // --- Sign Out ---
  const logout = useCallback(async () => {
    setError(null);
    try {
      await signOut(auth);
    } catch (err) {
      setError(err.message);
      console.error("Sign out failed:", err);
    }
  }, []);

  return {
    user,
    userId: user?.uid,
    isAnonymous: user?.isAnonymous,
    isLoading,
    error,
    signUpWithEmail,
    signInWithEmail,
    signInWithGoogle,
    signInWithApple,
    signInWithMicrosoft,
    continueAsGuest,
    logout,
  };
};

import type { HeroProjectData } from '../../../utils/hero/types';

interface HeroManifestEntry {
  id: string;
  title: string;
  duration: string;
  gradeLevel: string;
  subjects: string[];
  path: string;
}

interface HeroManifest {
  generatedAt: string;
  count: number;
  projects: HeroManifestEntry[];
}

import type { createRequire as CreateRequire } from 'module';

declare const module: {
  createRequire?: CreateRequire;
};

import manifestJson from './manifest.json';

const manifest = manifestJson as HeroManifest;

const isNodeRuntime = typeof process !== 'undefined' && Boolean(process.versions?.node);

function loadWithViteGlob(): Record<string, HeroProjectData> {
  try {
    return import.meta.glob('./*.json', {
      eager: true,
      import: 'default'
    }) as Record<string, HeroProjectData>;
  } catch {
    return {};
  }
}

function loadWithNodeFs(): Record<string, HeroProjectData> {
  if (!isNodeRuntime) {
    return {};
  }

  const createRequire = typeof module !== 'undefined' ? module.createRequire : undefined;
  if (typeof createRequire !== 'function') {
    return {};
  }

  const requireFn = createRequire(import.meta.url);
  const { readFileSync, readdirSync } = requireFn('node:fs') as typeof import('node:fs');
  const { fileURLToPath } = requireFn('node:url') as typeof import('node:url');
  const { dirname, resolve } = requireFn('node:path') as typeof import('node:path');

  const dir = dirname(fileURLToPath(import.meta.url));
  const files = readdirSync(dir).filter(file => file.endsWith('.json'));

  return Object.fromEntries(
    files.map(file => {
      const contents = readFileSync(resolve(dir, file), 'utf-8');
      return [`./${file}`, JSON.parse(contents) as HeroProjectData];
    })
  );
}

const projectModules: Record<string, HeroProjectData> = (() => {
  const viaVite = loadWithViteGlob();
  if (Object.keys(viaVite).length > 0) {
    return viaVite;
  }
  if (isNodeRuntime) {
    return loadWithNodeFs();
  }
  return {};
})();

const projectsById: Record<string, HeroProjectData> = Object.fromEntries(
  Object.entries(projectModules)
    .filter(([key]) => !key.endsWith('manifest.json'))
    .map(([key, value]) => {
      const id = key.replace('./', '').replace('.json', '');
      return [id, value as HeroProjectData];
    })
);

export function listGeneratedHeroProjects(): HeroManifestEntry[] {
  return manifest.projects;
}

export function getGeneratedHeroProject(id: string): HeroProjectData | undefined {
  return projectsById[id];
}

export function getGeneratedHeroManifest(): HeroManifest {
  return manifest;
}

import type { HeroProjectData } from '../../utils/hero/types';
import {
  listGeneratedHeroProjects,
  getGeneratedHeroProject
} from '../../data/generated/hero/loader';

export interface HeroPromptReference {
  id: string;
  title: string;
  subjects: string[];
  gradeLevel: string;
  duration: string;
  bigIdea: string;
  essentialQuestion: string;
  challenge: string;
  studentRole: string;
  authenticity: string;
  summary: string;
  milestoneHighlights: Array<{ id: string; title: string; description: string }>;
  assessmentFocus: string[];
  metricHighlights: Array<{ metric: string; target: string }>;
  impactSummary: string;
  resourceHighlights: string[];
}

interface HeroPromptCache {
  references: HeroPromptReference[];
  byId: Map<string, HeroPromptReference>;
  bySubject: Map<string, HeroPromptReference[]>;
  byGrade: Map<string, HeroPromptReference[]>;
}

export interface HeroPromptQueryOptions {
  limit?: number;
  subjects?: string[];
  gradeLevels?: string[];
  pinnedIds?: string[];
}

const SUBJECT_DELIMITERS = /[\s/&,+-]+/g;

function normaliseToken(value: string): string {
  return value.trim().toLowerCase();
}

function expandGradeTokens(gradeLevel: string): string[] {
  const tokens = new Set<string>();
  const normalised = normaliseToken(gradeLevel);
  tokens.add(normalised);
  if (normalised.includes('elementary')) tokens.add('elementary');
  if (normalised.includes('middle')) tokens.add('middle');
  if (normalised.includes('high')) tokens.add('high');
  if (normalised.includes('upper')) tokens.add('upper-secondary');
  if (normalised.includes('secondary')) tokens.add('secondary');
  if (normalised.includes('college') || normalised.includes('higher')) tokens.add('higher-ed');
  if (normalised.includes('adult')) tokens.add('adult');
  return Array.from(tokens);
}

function summariseProject(project: HeroProjectData): HeroPromptReference {
  const milestones = project.journey.milestones.slice(0, 3).map(milestone => ({
    id: milestone.id,
    title: milestone.title,
    description: milestone.description
  }));

  const assessments = project.assessment.rubric.slice(0, 3).map(rubric => rubric.category);

  const metricHighlights = project.impact?.metrics?.slice(0, 3).map(metric => ({
    metric: metric.metric,
    target: metric.target
  })) ?? [];

  const resourceHighlights = project.resources?.required?.slice(0, 2).map(resource => {
    const label = resource.category;
    const sample = resource.items?.[0];
    return sample ? `${label}: ${sample}` : label;
  }) ?? [];

  const impactSummary = project.impact?.community?.slice(0, 2).join(' ') ||
    project.impact?.personal?.slice(0, 2).join(' ') || '';

  return {
    id: project.id,
    title: project.title,
    subjects: project.subjects,
    gradeLevel: project.gradeLevel,
    duration: project.duration,
    bigIdea: project.bigIdea.statement,
    essentialQuestion: project.bigIdea.essentialQuestion,
    challenge: project.bigIdea.challenge,
    studentRole: project.context.studentRole,
    authenticity: project.context.authenticity,
    summary: project.courseAbstract.overview,
    milestoneHighlights: milestones,
    assessmentFocus: assessments,
    metricHighlights,
    impactSummary,
    resourceHighlights
  };
}

let heroPromptCache: HeroPromptCache | null = null;

function ensureCache(): HeroPromptCache {
  if (heroPromptCache) {
    return heroPromptCache;
  }

  const manifest = listGeneratedHeroProjects();
  const references = manifest
    .map(entry => getGeneratedHeroProject(entry.id))
    .filter((project): project is HeroProjectData => Boolean(project))
    .map(project => summariseProject(project));

  const byId = new Map<string, HeroPromptReference>();
  const bySubject = new Map<string, HeroPromptReference[]>();
  const byGrade = new Map<string, HeroPromptReference[]>();

  for (const reference of references) {
    byId.set(reference.id, reference);

    reference.subjects.forEach(subject => {
      const subjectTokens = subject.split(SUBJECT_DELIMITERS).map(normaliseToken).filter(Boolean);
      subjectTokens.forEach(token => {
        const list = bySubject.get(token) ?? [];
        list.push(reference);
        bySubject.set(token, list);
      });
    });

    expandGradeTokens(reference.gradeLevel).forEach(token => {
      const list = byGrade.get(token) ?? [];
      list.push(reference);
      byGrade.set(token, list);
    });
  }

  heroPromptCache = { references, byId, bySubject, byGrade };
  return heroPromptCache;
}

function addUnique(accumulator: HeroPromptReference[], candidates: HeroPromptReference[]) {
  for (const candidate of candidates) {
    if (!accumulator.some(existing => existing.id === candidate.id)) {
      accumulator.push(candidate);
    }
  }
}

export function queryHeroPromptReferences(options: HeroPromptQueryOptions = {}): HeroPromptReference[] {
  const { limit = 6, subjects = [], gradeLevels = [], pinnedIds = [] } = options;
  const cache = ensureCache();

  if (limit <= 0) {
    return [];
  }

  const results: HeroPromptReference[] = [];

  pinnedIds.forEach(id => {
    const ref = cache.byId.get(id);
    if (ref) addUnique(results, [ref]);
  });

  const normalisedSubjects = subjects.map(normaliseToken).filter(Boolean);
  for (const subject of normalisedSubjects) {
    const references = cache.bySubject.get(subject);
    if (references) addUnique(results, references);
  }

  const normalisedGrades = gradeLevels.flatMap(expandGradeTokens).map(normaliseToken).filter(Boolean);
  for (const grade of normalisedGrades) {
    const references = cache.byGrade.get(grade);
    if (references) addUnique(results, references);
  }

  if (results.length < limit) {
    addUnique(results, cache.references);
  }

  return results.slice(0, limit);
}

export function getHeroPromptReferences(limit = 6): HeroPromptReference[] {
  return queryHeroPromptReferences({ limit });
}

export function getHeroPromptReferenceById(id: string): HeroPromptReference | undefined {
  const cache = ensureCache();
  return cache.byId.get(id);
}

export function invalidateHeroPromptCache() {
  heroPromptCache = null;
}

/**
 * conversationFramework.ts
 * 
 * Better grounding and guidance for teachers using ALF
 */

export interface ConversationStage {
  id: string;
  name: string;
  description: string;
  readyForCards: boolean;
  messages: {
    initial?: string;
    followup?: string;
    transition?: string;
  };
}

export const CONVERSATION_STAGES: Record<string, ConversationStage> = {
  GROUNDING: {
    id: 'GROUNDING',
    name: 'Framework Introduction',
    description: 'Introduce the teacher to ALF and understand their context',
    readyForCards: false,
    messages: {
      initial: `# Welcome to ALF Coach!

I'm here to help you design an engaging project-based learning experience using the **Active Learning Framework (ALF)**.

## What is ALF?

ALF is a proven framework that guides students through the **Creative Process** to develop critical 21st-century skills. Your students will:

1. **Analyze** - Research and understand the problem space
2. **Brainstorm** - Generate creative solutions
3. **Prototype** - Build and test their ideas
4. **Evaluate** - Reflect and iterate on their work

## Let's Start with You

Before we design your project, I'd like to understand your teaching context better.

**What subject area do you teach?** 

*(For example: Science, Math, English, History, Art, etc.)*`,

      followup: `Great! I see you teach **{subject}**. 

Now, let's understand your students better so we can design an age-appropriate project.

**What grade level or age group are your students?**

*(For example: 9th grade, ages 14-15, high school seniors, etc.)*`,

      transition: `Perfect! You're teaching **{subject}** to **{gradeLevel}** students.

One more quick question before we start designing:

**How long do you have for this project?**

Consider:
- Quick sprint: 1-2 weeks
- Standard project: 3-4 weeks  
- Deep dive: 5-8 weeks
- Semester-long: 12+ weeks`
    }
  },

  IDEATION_INTRO: {
    id: 'IDEATION_INTRO',
    name: 'Ideation Introduction',
    description: 'Explain the ideation phase before showing cards',
    readyForCards: false,
    messages: {
      initial: `## Excellent! Let's Design Your Project

You're teaching **{subject}** to **{gradeLevel}** students for **{duration}**.

Now we'll work through three key elements that will form the foundation of your project:

### The Three Pillars of ALF

1. **Big Idea** - The overarching theme or concept that connects to real-world issues
2. **Essential Question** - The driving inquiry that guides student exploration
3. **Challenge** - The authentic task students will tackle

These work together to create meaningful, engaging learning experiences.

**Let's start with the Big Idea.**

Think about:
- What themes in your subject connect to students' lives?
- What concepts have real-world relevance?
- What would make students say "this matters"?

*What topic or theme would you like to explore with your students?*`
    }
  },

  BIG_IDEA: {
    id: 'BIG_IDEA',
    name: 'Big Idea Development',
    description: 'Help teacher develop their Big Idea',
    readyForCards: true,
    messages: {
      initial: `I can help you develop a compelling **Big Idea** around that topic.

A strong Big Idea should:
- Connect to real-world issues
- Be conceptual (not just a topic)
- Resonate with student interests
- Allow for multiple perspectives

Would you like me to suggest some Big Ideas, or do you have one in mind?`,

      followup: `That's a great starting point! Let me help you refine this into a powerful Big Idea.

**Current concept:** {userInput}

Here are some ways to strengthen it:

1. **Make it more conceptual** - Move from topic to theme
2. **Add relevance** - Connect to current events or student lives
3. **Broaden perspective** - Allow for multiple viewpoints

*How would you like to develop this further?*`
    }
  },

  ESSENTIAL_QUESTION: {
    id: 'ESSENTIAL_QUESTION',
    name: 'Essential Question',
    description: 'Craft the driving question',
    readyForCards: true,
    messages: {
      initial: `Excellent! Your Big Idea is taking shape:

> **{bigIdea}**

Now let's create an **Essential Question** that will drive student inquiry.

A powerful Essential Question:
- Is open-ended (no single right answer)
- Provokes deep thinking
- Connects to the Big Idea
- Matters to students

*What question would you want students to explore throughout this project?*`
    }
  },

  CHALLENGE: {
    id: 'CHALLENGE',
    name: 'Challenge Design',
    description: 'Define the authentic challenge',
    readyForCards: true,
    messages: {
      initial: `Perfect! We now have:

> **Big Idea:** {bigIdea}
> **Essential Question:** {essentialQuestion}

The final piece is the **Challenge** - the authentic task students will complete.

A meaningful Challenge should:
- Address a real problem or need
- Allow for creative solutions
- Connect to the community
- Result in a tangible outcome

*What real-world problem or task could students tackle that relates to your Big Idea and Essential Question?*`
    }
  }
};

export function getStageMessage(
  stage: string, 
  messageType: 'initial' | 'followup' | 'transition',
  context?: Record<string, string>
): string {
  const stageConfig = CONVERSATION_STAGES[stage];
  if (!stageConfig) return '';

  let message = stageConfig.messages[messageType] || '';

  // Replace placeholders with context values
  if (context) {
    Object.entries(context).forEach(([key, value]) => {
      message = message.replace(new RegExp(`{${key}}`, 'g'), value);
    });
  }

  return message;
}

export function shouldShowCards(stage: string, messageCount: number): boolean {
  const stageConfig = CONVERSATION_STAGES[stage];
  if (!stageConfig) return false;

  // Only show cards if:
  // 1. The stage is ready for cards
  // 2. We've had enough conversation to establish context
  // 3. User seems ready (based on message count)
  return stageConfig.readyForCards && messageCount >= 2;
}

export function getNextStage(currentStage: string): string {
  const stageOrder = [
    'GROUNDING',
    'IDEATION_INTRO', 
    'BIG_IDEA',
    'ESSENTIAL_QUESTION',
    'CHALLENGE',
    'JOURNEY',
    'DELIVERABLES'
  ];

  const currentIndex = stageOrder.indexOf(currentStage);
  if (currentIndex === -1 || currentIndex === stageOrder.length - 1) {
    return currentStage;
  }

  return stageOrder[currentIndex + 1];
}

import type { WizardData, WizardDataV3 } from '../../features/wizard/wizardSchema';

export interface CategoryResult {
  percentage: number;
  missing: string[];
}

export interface WizardCompletenessResult {
  core: CategoryResult;
  context: CategoryResult;
  progressive: CategoryResult;
  summary: {
    core: number;
    context: number;
    progressive: number;
    overall: number;
  };
}

type WizardInput = Partial<WizardData> | Partial<WizardDataV3>;

type Requirement = {
  label: string;
  weight?: number;
  check: (data: WizardInput) => boolean;
};

const DEFAULT_WEIGHT = 1;

const CORE_REQUIREMENTS: Requirement[] = [
  {
    label: 'Project topic',
    check: data => typeof data.projectTopic === 'string' && data.projectTopic.trim().length >= 20
  },
  {
    label: 'Learning goals',
    check: data => {
      const v3 = data as WizardDataV3;
      if (Array.isArray(v3.learningGoals)) {
        return v3.learningGoals.length > 0 && v3.learningGoals.every(goal => {
          if (!goal) return false;
          if (typeof (goal as any).text === 'string') {
            return (goal as any).text.trim().length > 0;
          }
          return typeof goal === 'string' && goal.trim().length > 0;
        });
      }
      const legacy = (data as WizardData).learningGoals;
      return typeof legacy === 'string' && legacy.trim().length >= 20;
    }
  },
  {
    label: 'Vision statement',
    check: data => typeof (data as any).vision === 'string' && (data as any).vision.trim().length >= 20
  },
  {
    label: 'Essential question',
    check: data => {
      const v3 = data as WizardDataV3;
      if (typeof v3?.essentialQuestion === 'string') {
        return v3.essentialQuestion.trim().length >= 10;
      }
      if (v3?.essentialQuestion && typeof (v3 as any).essentialQuestion?.text === 'string') {
        return (v3 as any).essentialQuestion.text.trim().length >= 10;
      }
      const legacy = (data as any).drivingQuestion || (data as any).essentialQuestion;
      return typeof legacy === 'string' && legacy.trim().length >= 10;
    }
  }
];

const CONTEXT_REQUIREMENTS: Requirement[] = [
  {
    label: 'Subjects',
    check: data => Array.isArray(data.subjects) && data.subjects.length > 0
  },
  {
    label: 'Primary subject',
    check: data => typeof data.primarySubject === 'string' && data.primarySubject.trim().length > 0
  },
  {
    label: 'Grade level',
    check: data => typeof data.gradeLevel === 'string' && data.gradeLevel.trim().length > 0
  },
  {
    label: 'Project duration',
    check: data => typeof data.duration === 'string' && data.duration.trim().length > 0
  },
  {
    label: 'Project context details',
    check: data => {
      const context = (data as WizardDataV3).projectContext;
      if (!context) return false;
      const hasSchedule = Boolean(context.schedule);
      const hasTech = Array.isArray((context as any).availableTech) && (context as any).availableTech.length > 0;
      const hasResources = Array.isArray(context.resources) && context.resources.length > 0;
      return hasSchedule || hasTech || hasResources;
    }
  }
];

const PROGRESSIVE_REQUIREMENTS: Requirement[] = [
  {
    label: 'Standards alignment',
    check: data => {
      const standards = (data as WizardDataV3).standardsAlignment;
      return Boolean(standards && Array.isArray(standards.standards) && standards.standards.length > 0);
    }
  },
  {
    label: 'Phases mapped',
    check: data => Array.isArray((data as WizardDataV3).phases) && Boolean((data as WizardDataV3).phases?.length)
  },
  {
    label: 'Milestones defined',
    check: data => Array.isArray((data as WizardDataV3).milestones) && Boolean((data as WizardDataV3).milestones?.length)
  },
  {
    label: 'Artifacts or deliverables',
    check: data => Array.isArray((data as WizardDataV3).artifacts) && Boolean((data as WizardDataV3).artifacts?.length)
  },
  {
    label: 'Rubrics or assessments',
    check: data => Array.isArray((data as WizardDataV3).rubrics) && Boolean((data as WizardDataV3).rubrics?.length)
  },
  {
    label: 'Differentiation supports',
    check: data => Boolean((data as WizardDataV3).differentiation)
  },
  {
    label: 'Evidence plan',
    check: data => Boolean((data as WizardDataV3).evidencePlan)
  },
  {
    label: 'Communications plan',
    check: data => Boolean((data as WizardDataV3).communications)
  },
  {
    label: 'Exhibition plan',
    check: data => Boolean((data as WizardDataV3).exhibition)
  }
];

function evaluateCategory(requirements: Requirement[], data: WizardInput): CategoryResult {
  let totalWeight = 0;
  let completedWeight = 0;
  const missing: string[] = [];

  requirements.forEach(requirement => {
    const weight = requirement.weight ?? DEFAULT_WEIGHT;
    totalWeight += weight;
    const satisfied = requirement.check(data);
    if (satisfied) {
      completedWeight += weight;
    } else {
      missing.push(requirement.label);
    }
  });

  const percentage = totalWeight === 0 ? 0 : Math.round((completedWeight / totalWeight) * 100);
  return { percentage, missing };
}

export function evaluateWizardCompleteness(data: WizardInput): WizardCompletenessResult {
  const core = evaluateCategory(CORE_REQUIREMENTS, data);
  const context = evaluateCategory(CONTEXT_REQUIREMENTS, data);
  const progressive = evaluateCategory(PROGRESSIVE_REQUIREMENTS, data);
  const overall = Math.round((core.percentage + context.percentage + progressive.percentage) / 3);

  return {
    core,
    context,
    progressive,
    summary: {
      core: core.percentage,
      context: context.percentage,
      progressive: progressive.percentage,
      overall
    }
  };
}

import { auth } from '../firebase/firebase';

/**
 * Wait for authentication to be ready
 * @param {number} timeout - Maximum time to wait in milliseconds
 * @returns {Promise<boolean>} Whether auth is ready
 */
export async function waitForAuth(timeout = 5000) {
  const startTime = Date.now();

  return new Promise((resolve) => {
    // If already authenticated, resolve immediately
    if (auth.currentUser) {
      resolve(true);
      return;
    }

    // Listen for auth state changes
    const unsubscribe = auth.onAuthStateChanged((user) => {
      if (user) {
        unsubscribe();
        resolve(true);
      } else if (Date.now() - startTime > timeout) {
        unsubscribe();
        resolve(false);
      }
    });

    // Timeout fallback
    setTimeout(() => {
      unsubscribe();
      resolve(false);
    }, timeout);
  });
}

/**
 * Safely perform Firestore operations with auth check
 * @param {Function} operation - The Firestore operation to perform
 * @param {string} errorMessage - Custom error message
 * @param {boolean} requireAuth - Whether to require authentication (default: true)
 * @returns {Promise<any>} The result of the operation
 */
export async function safeFirestoreOperation(operation, errorMessage = "Firestore operation failed", requireAuth = true) {
  try {
    // Wait for auth to be ready if required
    if (requireAuth) {
      const authReady = await waitForAuth();
      if (!authReady && !auth.currentUser) {
        console.warn("No authenticated user for Firestore operation. Using localStorage fallback.");
        return null;
      }
    }

    // Perform the operation
    return await operation();
  } catch (error) {
    console.error(errorMessage, error);

    // Check for specific error types
    if (error.code === 'permission-denied') {
      console.error("Firestore permission denied. Check security rules and authentication.");
      console.error("Current user:", auth.currentUser?.uid || 'none');
    } else if (error.code === 'unavailable') {
      console.error("Firestore service unavailable. Check network connection.");
    } else if (error.message?.includes('400')) {
      console.error("Bad request to Firestore. Check data format.");
    }

    // Don't throw the error, return null to trigger fallback
    return null;
  }
}

/**
 * Cleanup function for Firestore listeners
 * @param {Function|null} unsubscribe - The unsubscribe function
 */
export function cleanupFirestoreListener(unsubscribe) {
  if (unsubscribe && typeof unsubscribe === 'function') {
    try {
      unsubscribe();
    } catch (error) {
      console.error("Error cleaning up Firestore listener:", error);
    }
  }
}

import { safeFirestoreOperation, waitForAuth } from './firestoreHelpers';

// Note: We'll import and use the error reporter where this is used, not here
// to avoid circular dependencies

/**
 * Retry configuration
 */
const RETRY_CONFIG = {
  maxAttempts: 3,
  initialDelay: 1000, // 1 second
  maxDelay: 5000, // 5 seconds
  backoffMultiplier: 2
};

/**
 * Sleep for specified milliseconds
 */
const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));

/**
 * Perform a Firestore operation with retry logic
 * @param {Function} operation - The Firestore operation to perform
 * @param {Object} options - Options for the operation
 * @param {string} options.errorMessage - Custom error message
 * @param {boolean} options.requireAuth - Whether to require authentication
 * @param {number} options.maxAttempts - Maximum retry attempts
 * @param {Function} options.fallback - Fallback function if all retries fail
 * @returns {Promise<any>} The result of the operation
 */
export async function firestoreOperationWithRetry(
  operation,
  {
    errorMessage = "Firestore operation failed",
    requireAuth = true,
    maxAttempts = RETRY_CONFIG.maxAttempts,
    fallback = null
  } = {}
) {
  let lastError = null;
  let delay = RETRY_CONFIG.initialDelay;

  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
    try {
      // Use safeFirestoreOperation for auth checks
      const result = await safeFirestoreOperation(operation, errorMessage, requireAuth);

      // If we got a result (not null), return it
      if (result !== null) {
        return result;
      }

      // If null was returned, it means auth failed or operation failed
      // Try fallback if this is the last attempt
      if (attempt === maxAttempts && fallback) {
        console.log("All Firestore attempts failed, using fallback");
        return await fallback();
      }

      throw new Error("Operation returned null");

    } catch (error) {
      lastError = error;

      // Don't retry on permission errors - they won't succeed
      if (error.code === 'permission-denied') {
        console.error("Permission denied, not retrying");
        if (fallback) {
          return await fallback();
        }
        throw error;
      }

      // Check if we should retry
      if (attempt < maxAttempts) {
        console.log(`Attempt ${attempt} failed, retrying in ${delay}ms...`);
        await sleep(delay);

        // Exponential backoff
        delay = Math.min(delay * RETRY_CONFIG.backoffMultiplier, RETRY_CONFIG.maxDelay);

        // Wait for auth to be ready before next attempt
        if (requireAuth) {
          await waitForAuth(5000);
        }
      }
    }
  }

  // All attempts failed
  console.error(`All ${maxAttempts} attempts failed:`, lastError);

  if (fallback) {
    console.log("Using fallback after all retries failed");
    return await fallback();
  }

  throw lastError;
}

/**
 * Helper to create a localStorage fallback
 * @param {string} key - The localStorage key
 * @param {any} data - The data to store
 * @param {string} operation - The operation type ('get', 'set', 'delete')
 */
export function createLocalStorageFallback(key, data = null, operation = 'get') {
  return async () => {
    try {
      switch (operation) {
        case 'get':
          const stored = localStorage.getItem(key);
          return stored ? JSON.parse(stored) : null;

        case 'set':
          localStorage.setItem(key, JSON.stringify(data));
          return data;

        case 'delete':
          localStorage.removeItem(key);
          return true;

        default:
          throw new Error(`Unknown operation: ${operation}`);
      }
    } catch (error) {
      console.error("LocalStorage fallback error:", error);
      return null;
    }
  };
}

