    const audience = cd['deliverables.impact.audience'] || 'audience TBD';
    const method = cd['deliverables.impact.method'] || 'sharing method TBD';
    const parts: string[] = [];
    parts.push(`${milestones} milestone${milestones===1?'':'s'}`);
    parts.push(criteria);
